import ItemVitrine from '../itemVitrine';
import "./style.css"


function Vitrine({ produtos, carrinho, setCarrinho }) {

    function inserirCarrinho(itemASerInserido) {
        if ((carrinho.filter((item) => itemASerInserido === item)).length === 0) {

            setCarrinho([...carrinho, { name: itemASerInserido.name, category: itemASerInserido.category, img: itemASerInserido.img, quantidade: 1 }])
            console.log(carrinho)
        }
    }

    return (
        <div className="produtos">
            <div className="products">
                {produtos.map((item) =>
                    <ItemVitrine inserirCarrinho={inserirCarrinho} key={item.id} product={item} />
                )}
            </div>
        </div>
    );

}


export default Vitrine
