import './style.css';
import ItemCarrinho from '../ItemCarrinho';
import { useState } from 'react';

function Carrinho({ }) {
    const [carrinho, setCarrinho] = useState([{ id: 1, name: 'Hamburguer', category: 'Sanduíches', price: 14.00, img: 'https://i.ibb.co/fpVHnZL/hamburguer.png' }])

    function removeCarrinho(itemASerRemovido) {
        setCarrinho(carrinho.filter((item) => item !== itemASerRemovido))
    }


    return (
        <div>
            <div className="carrinho">
                <div className="title-carrinho">
                    <h1>Carrinho de compras</h1>
                </div>

                <div className="itens-carrinho">
                    {carrinho.length === 0 ?
                        <div className="itens-carrinho-vazio">
                            <h2>Sua sacola está vazia</h2>
                            <span>Adicione itens</span>
                        </div>
                        :
                        <div>
                            {
                                carrinho.map((item) => (
                                    <ItemCarrinho removerCarrinho={removeCarrinho} key={item.id} product={item} />
                                ))
                            }
                        </div>
                    }
                </div>
            </div>
        </div>
    )

}

export default Carrinho;
