import './style.css';
import ItemCarrinho from '../ItemCarrinho';
import { useState } from 'react';
import Button from '../Button';

function Carrinho({ carrinho, setCarrinho, total }) {

    function removerCarrinho(itemASerRemovido) {
        setCarrinho(carrinho.filter((item) => item !== itemASerRemovido))
    }



    return (
        <div>
            <div className="carrinho">
                <div className="title-carrinho">
                    <h1>Carrinho de compras</h1>
                </div>

                <div className="itens-carrinho">
                    {carrinho.length === 0 ?
                        <div className="itens-carrinho-vazio">
                            <h2>Sua sacola est√° vazia</h2>
                            <span>Adicione itens</span>
                        </div>
                        :
                        <div>
                            {
                                carrinho.map((item) => (
                                    <ItemCarrinho removerCarrinho={removerCarrinho} key={item.id} product={item} />
                                ))
                            }
                        </div>
                    }
                </div>
                <div>
                    <div>
                        <span>Total</span>
                        <span>{(total.toFixed(2)).toString().replace(".", ",")}</span>
                    </div>
                    <Button text="Remover todos"></Button>
                </div>
            </div>
        </div>
    )

}

export default Carrinho;
